mapper.select:
SELECT c.complain_no, p.payment_no, c.complain_category, c.complain_title, c.create_date, c.update_date, m.member_name, a.accom_name, ro.room_name FROM complain c LEFT JOIN payment p ON c.payment_no = p.payment_no LEFT JOIN reserve r ON p.reserve_no = r.reserve_no LEFT JOIN room ro ON ro.room_no = r.room_no LEFT JOIN accom a ON a.accom_no = ro.accom_no LEFT JOIN member m ON r.member_no = m.member_no LEFT JOIN complain_comment cc ON cc.complain_no = c.complain_no
a.host_no = #{hostNo} AND cc.complain_comment_content IS NULL AND c.complain_category = #{complainCategory}
a.host_no = #{hostNo} AND cc.complain_comment_content IS NULL
cc.complain_comment_content IS NULL AND c.complain_category = #{complainCategory}
cc.complain_comment_content IS NULL
ORDER BY c.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM complain c LEFT JOIN payment p ON c.payment_no = p.payment_no LEFT JOIN reserve r ON p.reserve_no = r.reserve_no LEFT JOIN room ro ON ro.room_no = r.room_no LEFT JOIN accom a ON a.accom_no = ro.accom_no LEFT JOIN member m ON r.member_no = m.member_no LEFT JOIN complain_comment cc ON cc.complain_no = c.complain_no
a.host_no = #{hostNo} AND cc.complain_comment_content IS NULL AND c.complain_category = #{complainCategory}
a.host_no = #{hostNo} AND cc.complain_comment_content IS NULL
cc.complain_comment_content IS NULL AND c.complain_category = #{complainCategory}
cc.complain_comment_content IS NULL

SELECT c.complain_no, p.payment_no, c.complain_category, c.complain_title, c.create_date, c.update_date, m.member_name, a.accom_name, ro.room_name, cc.complain_comment_content FROM complain c LEFT JOIN payment p ON c.payment_no = p.payment_no LEFT JOIN reserve r ON p.reserve_no = r.reserve_no LEFT JOIN room ro ON ro.room_no = r.room_no LEFT JOIN accom a ON a.accom_no = ro.accom_no LEFT JOIN member m ON r.member_no = m.member_no LEFT JOIN complain_comment cc ON cc.complain_no = c.complain_no
a.host_no = #{hostNo} AND c.complain_category = #{complainCategory}
a.host_no = #{hostNo}
c.complain_category = #{complainCategory}
ORDER BY c.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM complain c LEFT JOIN payment p ON c.payment_no = p.payment_no LEFT JOIN reserve r ON p.reserve_no = r.reserve_no LEFT JOIN room ro ON ro.room_no = r.room_no LEFT JOIN accom a ON a.accom_no = ro.accom_no LEFT JOIN member m ON r.member_no = m.member_no LEFT JOIN complain_comment cc ON cc.complain_no = c.complain_no
a.host_no = #{hostNo} AND c.complain_category = #{complainCategory}
a.host_no = #{hostNo}
c.complain_category = #{complainCategory}

mapper.delete:
DELETE 
FROM complain 
WHERE complain_no = #{complainNo} AND payment_no = #{paymentNo}
