SELECT hq.host_qna_no, hq.host_no, hq.host_qna_category, hq.host_qna_title, hq.host_qna_upload_file, hq.create_date, h.host_name FROM host_qna hq LEFT JOIN host h ON hq.host_no = h.host_no LEFT JOIN host_qna_comment hqc ON hq.host_qna_no = hqc.host_qna_no
hq.host_no = #{hostNo} AND hqc.admin_no IS NULL AND hq.host_qna_category = #{hostQnaCategory}
hq.host_no = #{hostNo} AND hqc.admin_no IS NULL
hqc.admin_no IS NULL AND hq.host_qna_category = #{hostQnaCategory}
hqc.admin_no IS NULL
ORDER BY hq.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM host_qna hq LEFT JOIN host_qna_comment hqc ON hq.host_qna_no = hqc.host_qna_no
hq.host_no = #{hostNo} AND hqc.admin_no IS NULL AND hq.host_qna_category = #{hostQnaCategory}
hq.host_no = #{hostNo} AND hqc.admin_no IS NULL
hqc.admin_no IS NULL AND hq.host_qna_category = #{hostQnaCategory}
hqc.admin_no IS NULL

SELECT hq.host_qna_no, hq.host_no, hq.host_qna_category, hq.host_qna_state, hq.host_qna_title, hq.host_qna_content, hq.host_qna_upload_file, hq.create_date, h.host_name, hqc.admin_no FROM host_qna hq LEFT JOIN host h ON hq.host_no = h.host_no LEFT JOIN host_qna_comment hqc ON hq.host_qna_no = hqc.host_qna_no
hq.host_no = #{hostNo} AND hq.host_qna_category = #{hostQnaCategory}
hq.host_no = #{hostNo}
hq.host_qna_category = #{hostQnaCategory}
ORDER BY hq.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

