mapper.select:
SELECT re.reserve_no, m.member_no, r.room_no, m.member_name, ac.accom_name, r.room_name, p.payment_method, p.payment_price, re.reserve_use, re.reserve_info, re.reserve_personnel, re.reserve_date, re.reserve_cancel_date, re.reserve_check_in, re.reserve_check_out FROM reserve re LEFT JOIN payment p ON p.reserve_no = re.reserve_no LEFT JOIN member m ON re.member_no = m.member_no LEFT JOIN room r ON r.room_no = re.room_no LEFT JOIN accom ac ON r.accom_no = ac.accom_no LEFT JOIN host h ON ac.host_no = h.host_no
h.host_no = #{hostNo}
AND re.reserve_use = #{reserveUse}
ORDER BY re.reserve_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM reserve re LEFT JOIN room ro ON ro.room_no = re.room_no LEFT JOIN accom a ON a.accom_no = ro.accom_no LEFT JOIN host h ON a.host_no = h.host_no LEFT JOIN payment p ON p.reserve_no = re.reserve_no LEFT JOIN member m ON re.member_no = m.member_no
h.host_no = #{hostNo}
AND reserve_use = #{reserveUse}
